import React, { Component } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { Login } from "./Login/Login";
import LoginView from "./Login/LoginView";
import { Dashboard } from "./Dashboard/Dashboard";
// import logo from "./img/logo-bianco.png";
import Cookies from "universal-cookie";
import "./App.css";

const cookies = new Cookies();
const login = new Login();
const dashboardRender = new Dashboard();

class App extends Component {
  state = {
    username: "",
    password: "",
    environment: "",
    loginstatus: false,
    loading: true
  };

  inputHandler = event => {
    let name = event.target.name;
    this.setState({ [name]: event.target.value });
  };

  handlerLogin = event => {
    login.authentication(
      this.state.username,
      this.state.password,
      this.state.environment,
      this.setAuthorization
    );
  };

  setAuthorization = auth => {
    if (auth === false) {
      console.log("NON autorizzato");
      this.setState({ loginstatus: auth, loading: false });
    } else {
      console.log("autorizzato");
      this.setState({ loginstatus: auth, loading: false });
    }
  };

  render() {
    return (
      <div>
        {this.state.loading && !this.state.loginstatus && (
          <div>
            <h1> Loading </h1>
          </div>
        )}
        {!this.state.loading && !this.state.loginstatus && (
          <div>
            <div>{/* <img src={logo} alt="movingtech" /> */}</div>
            <div>
              <input
                // className="login-input"
                type="text"
                name="username"
                placeholder="Username"
                value={this.state.username}
                onChange={this.inputHandler}
              />
              <span />
            </div>
            <div>
              <input
                // className="login-input"
                type="password"
                name="password"
                placeholder="Password"
                value={this.state.password}
                onChange={this.inputHandler}
              />
              <span />
            </div>
            <div>
              <input
                // className="login-input"
                type="text"
                name="environment"
                placeholder="Environment"
                value={this.state.environment}
                onChange={this.inputHandler}
              />
              <span />
            </div>
            <div>
              <input
                // className="mucca-buttom"
                type="submit"
                value="login"
                onClick={this.handlerLogin}
              />
            </div>
          </div>
        )}
        {!this.state.loading && this.state.loginstatus && (
          <Router>{dashboardRender.renderDashboard()}</Router>
        )}
      </div>
    );
  }

  componentWillMount() {
    if (
      typeof cookies.get("userToken") != "undefined" &&
      cookies.get("userToken")
    ) {
      if (
        typeof cookies.get("userKey") != "undefined" &&
        cookies.get("userKey")
      ) {
        login.authorization(
          cookies.get("userToken"),
          cookies.get("userKey"),
          this.setAuthorization
        );
      }
    } else {
      console.log("start FAKE authorization");
      login.authorization("noToken", "noKey", this.setAuthorization);
    }
  }
}

export default App;
