import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import { Header, Icon, Container, Button, Table } from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();

class AppConfig extends Component {
  state = {
    environment: cookies.get("userEnv"),
    appInfo: "",
    core: "",
    mpkg: "",
    storage: "",
    loaded: false
  };

  getConfigData = res => {
    if (res && res.status === 200) {
      console.log(res.data.data);
      this.setState({
        appInfo: res.data.data.appinfo,
        core: res.data.data.core,
        mpkg: res.data.data.mpkg,
        storage: res.data.data.storage,
        loaded: true
      });
    }
  };

  setAppInfo() {
    console.log(this.state.appInfo);
    return (
      <Container fluid>
        <Header textAlign="center" color="violet">
          {this.state.appInfo.name}{" "}
        </Header>
      </Container>
    );
  }
  setCore() {
    console.log(this.state.core);
    return (
      <Table
        color="violet"
        //   key={color}
      >
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Name</Table.HeaderCell>
            <Table.HeaderCell>Port</Table.HeaderCell>
            <Table.HeaderCell>Status</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          <Table.Row>
            <Table.Cell>Apples</Table.Cell>
            <Table.Cell>200</Table.Cell>
            <Table.Cell>0g</Table.Cell>
          </Table.Row>
          <Table.Row>
            <Table.Cell>Orange</Table.Cell>
            <Table.Cell>310</Table.Cell>
            <Table.Cell>0g</Table.Cell>
          </Table.Row>
        </Table.Body>
      </Table>
    );
  }
  setMpkg() {}

  setStorage() {}

  render() {
    return (
      <div className="appConfig">{this.state.loaded && this.setAppInfo()}</div>
    );
  }
  componentWillMount() {
    if (this.state.environment != undefined) {
      let query = "config";
      api.read(this.state.environment, query, this.getConfigData);
    }
  }
}

export default AppConfig;
