import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import {
  Header,
  Icon,
  Container,
  Button,
  Modal,
  Label,
  Accordion,
  Grid,
  Segment,
  Transition,
  Form,
  Radio,
  Card
} from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();
// const ws = new WebSocket("ws://localhost:8081");

class LogsViewer extends Component {
  state = {
    environment: cookies.get("userEnv"),
    port: cookies.get("logPort"),
    file: cookies.get("logFile"),
    action: cookies.get("logAction"),
    loaded: false,
    data: []
  };

  openWebSocket = () => {
    console.log("openind websocket");
    var ws = new WebSocket("ws://localhost:8081");

    ws.onopen = () => {
      // connection opened
      let toSend = JSON.stringify({ filename: "2019517_8715.log" });

      ws.send(toSend); // send a message
    };

    ws.onmessage = e => {
      // a message was received
      console.log(e.data);
      this.setState({ loaded: true });
      let stamp = this.state.data;
      if (e.data) {
        stamp.push(e.data);
        this.setState({ data: stamp });
      }
    };

    ws.onerror = e => {
      // an error occurred
      console.log(e.message);
    };

    ws.onclose = e => {
      // connection closed
      console.log(e.code, e.reason);
    };
  };

  render() {
    return (
      <div>
        Logs
        {this.state.loaded && this.state.data}
      </div>
    );
  }
  componentWillMount() {
    this.openWebSocket();
    console.log(
      cookies.get("logPort"),
      cookies.get("logFile"),
      cookies.get("logAction")
    );
    if (
      cookies.get("logPort") !== undefined &&
      cookies.get("logFile") !== undefined &&
      cookies.get("logAction") !== undefined
    ) {
      console.log("creating websockets");
    }
  }
}

export default LogsViewer;
