import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import { Header, Icon, Container, Button } from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();

class AppConfig extends Component {
  state = {
    environment: cookies.get("userEnv"),
    appInfo: "",
    core: "",
    mpkg: "",
    storage: "",
    loaded: false
  };

  getConfigData = res => {
    if (res && res.status === 200) {
      console.log(res.data.data);
      this.setState({
        appInfo: res.data.data.appinfo,
        core: res.data.data.core,
        mpkg: res.data.data.mpkg,
        storage: res.data.data.storage,
        loaded: true
      });
    }
  };

  setAppInfo() {
    console.log(this.state.appInfo);
    return (
      <Header textAlign="center" color="violet">
        {this.state.appInfo.name}{" "}
      </Header>
    );
  }
  setCore() {}
  setMpkg() {}

  setStorage() {}

  render() {
    return (
      <div className="appConfig">
        <p>Config</p>
        {this.state.loaded && this.setAppInfo()}
      </div>
    );
  }
  componentWillMount() {
    if (this.state.environment != undefined) {
      let query = "config";
      api.read(this.state.environment, query, this.getConfigData);
    }
  }
}

export default AppConfig;
