import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import {
  Header,
  Icon,
  Container,
  Button,
  Table,
  Label,
  Accordion,
  Grid,
  Segment
} from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();

class AppConfig extends Component {
  state = {
    environment: cookies.get("userEnv"),
    appInfo: "",
    superowner: "",
    core: "",
    mpkg: "",
    storage: "",
    portList: "",
    coreEventConsumer: "",
    mpkgEventConsumer: "",
    loaded: false,
    active0: true,
    active1: false,
    active2: false,
    active3: false,
    active4: false
  };

  handleClick0 = e => {
    this.setState({ active0: !this.state.active0 });
  };

  handleClick1 = e => {
    this.setState({ active1: !this.state.active1 });
  };
  handleClick2 = e => {
    this.setState({ active2: !this.state.active2 });
  };
  handleClick3 = e => {
    this.setState({ active3: !this.state.active3 });
  };
  handleClick4 = e => {
    this.setState({ active4: !this.state.active4 });
  };
  getConfigData = res => {
    if (res && res.status === 200) {
      this.setState({
        appInfo: res.data.data.appinfo,
        core: res.data.data.core,
        mpkg: res.data.data.mpkg,
        storage: res.data.data.storage,
        superowner: res.data.data.superowner,
        coreEventConsumer: res.data.data.coreEventConsumer,
        mpkgEventConsumer: res.data.data.mpkgEventConsumer,
        loaded: true
      });
    }
  };

  getPortList = res => {
    if (res && res.status === 200) {
      this.setState({ portList: res.data.data[this.state.environment] });
    }
  };

  setAppInfo() {
    return (
      <Table color="violet">
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>App Name</Table.HeaderCell>
            <Table.HeaderCell>Owner</Table.HeaderCell>
            <Table.HeaderCell>Environment</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          <Table.Row>
            <Table.Cell>{this.state.appInfo.name}</Table.Cell>
            <Table.Cell> {this.state.superowner}</Table.Cell>
            <Table.Cell> {this.state.environment}</Table.Cell>
          </Table.Row>
        </Table.Body>
      </Table>
    );
  }
  setCore() {
    const content = [];
    for (let x in this.state.core) {
      let rowTitle = (
        <Table.Row>
          <Table.Cell>{this.state.core[x].name}</Table.Cell>
          <Table.Cell>
            {this.state.portList[this.state.core[x].name]}
          </Table.Cell>
          <Table.Cell>
            <Icon name="checkmark" />
          </Table.Cell>
        </Table.Row>
      );
      content.push(rowTitle);
    }
    return (
      <Table color="teal">
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Service Name</Table.HeaderCell>
            <Table.HeaderCell>Port</Table.HeaderCell>
            <Table.HeaderCell>Status</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        {content.map((value, ind) => (
          <Table.Body key={ind}>{value}</Table.Body>
        ))}
      </Table>
    );
  }
  setMpkg() {
    const content = [];
    for (let x in this.state.mpkg) {
      let rowTitle = (
        <Table.Row>
          <Table.Cell>{this.state.mpkg[x].name}</Table.Cell>
          <Table.Cell>
            {this.state.portList[this.state.mpkg[x].name]}
          </Table.Cell>
          <Table.Cell>
            <Icon name="checkmark" />
          </Table.Cell>
        </Table.Row>
      );
      content.push(rowTitle);
    }
