import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import {
  Header,
  Icon,
  Container,
  Button,
  Table,
  Label,
  Accordion,
  Grid,
  Segment,
  Transition,
  Form,
  Radio
} from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();

const options = [
  { key: "string", text: "string", value: "string" },
  { key: "int", text: "int", value: "int" },
  { key: "boolean", text: "boolean", value: "boolean" }
];

class AddService extends Component {
  state = {
    active0: true,
    duration: 200,
    count: 5,
    sName: "",
    pName: "",
    pType: "string",
    value: "3",
    pRequired: false,
    obj: {}
  };

  //Segment Visibility Handlers
  handleClick0 = e => {
    this.setState({
      active0: !this.state.active0
    });
  };

  typeHandler = (event, { value }) => {
    console.log("TYPE", value);
    this.setState({ pType: value });
  };

  handleChange = (e, { name, value }) => {
    console.log("HANDLE CHANGE", name, value);
    this.setState({ [name]: value });
  };

  handleReq = (e, { value }) => {
    console.log("REQ", value);
    this.setState({ value: value });
    if (value === 1) {
      return this.setState({ pRequired: true });
    } else {
      return this.setState({ pRequired: true });
    }
  };

  propertyObj = () => {
    let obj = {
      required: [],
      modelname: "",
      properties: {}
    };
    this.setState({ obj: obj });
  };
  addProperty = e => {
    let prev = this.state.obj;
    let sName = this.state.sName;
    let pName = this.state.pName;
    let prevReq = prev.required;
    let prevProp = prev.properties;
    if (this.state.pRequired) {
      prevReq.push(this.state.pName);
    }
    if (this.state.pName && this.state.pType) {
      // let ang = {
      //   bsonType: this.state.pType,
      //   description: pName
      // };
      let prp = {
        pName: {
          bsonType: this.state.pType,
          description: pName
        }
      };
      Object.assign(prevProp, prp);
      // prevProp.push(prp);
    }
    let newObj = {
      required: prevReq,
      modelname: sName,
      properties: prevProp
    };
    console.log("OBJ", this.state.obj);
    this.setState({ obj: newObj });
    console.log("OBJ", this.state.obj);
  };

  generateProp = () => {
    // da utilizzare per visualizzare le proprietÃ  salvate
    const content = [];
    let form = (
      <Segment>
        <Form.Group widths="equal">
          <Form.Input
            label="Property Name"
            placeholder="Property name"
            required
            onChange={this.handleChange}
          />
          <Form.Select
            fluid
            label="Property type"
            options={options}
            placeholder="Property type"
            onChange={this.typeHandler}
          />
        </Form.Group>
        <Form.Group inline>
          <label>Required</label>
          <Form.Field
            control={Radio}
            label="Yes"
            value="1"
            checked={this.state.value === "1"}
            // onChange={this.handleChange}
          />
          <Form.Field
            control={Radio}
            label="No"
            value="2"
            checked={this.state.value === "2"}
            // onChange={this.handleChange}
          />
        </Form.Group>
      </Segment>
    );

    for (let x = 0; x < this.state.count; x++) {
      content.push(form);
      console.log(content);
    }
    return content.map((value, ind) => <Segment key={ind}>{value}</Segment>);
  };

  render() {
    return (
      <div>
        <Accordion exclusive={false}>
          <Accordion.Title onClick={this.handleClick0}>
            <Header as="h3" color="green">
              Add New Service
              <Icon
                name="angle down"
                flipped={this.state.active0 ? "vertically" : "horizontally"}
              />
            </Header>
          </Accordion.Title>
          <Transition
            animation="slide down"
            duration={this.state.duration}
            visible={this.state.active0}
          >
            <Accordion.Content active={this.state.active0}>
              <Segment color="green">
                <Form>
                  <Form.Group widths="equal">
                    <Form.Input
                      label="Service name"
                      placeholder="Service name"
                      name="sName"
                      onChange={this.handleChange}
                      required
                    />
                  </Form.Group>
                  <Header as="h4"> Add Properties:</Header>
                  <Segment>
                    <Form.Group widths="equal">
                      <Form.Input
                        label="Property Name"
                        placeholder="Property name"
                        name="pName"
                        onChange={this.handleChange}
                        required
                      />
                      <Form.Select
                        fluid
                        label="Property type"
                        options={options}
                        placeholder="Property type"
                        onChange={this.typeHandler}
                      />
                    </Form.Group>
                    <Form.Group inline>
                      <label>Required</label>
                      <Form.Field
                        control={Radio}
                        label="Yes"
                        value="1"
                        checked={this.state.value === "1"}
                        onChange={this.handleReq}
                      />
                      <Form.Field
                        control={Radio}
                        label="No"
                        value="2"
                        checked={this.state.value === "2"}
                        onChange={this.handleReq}
                      />
                    </Form.Group>
                    <Button
                      type="submit"
                      basic
                      color="violet"
                      // compact
                      onClick={this.addProperty}
                    >
                      Add
                    </Button>
                  </Segment>
                  <Header as="h4"> Properties Added:</Header>
                  {/* {this.generateProp()} */}
                </Form>
              </Segment>
            </Accordion.Content>
          </Transition>
        </Accordion>
      </div>
    );
  }
  componentWillMount() {
    console.log("will mount");
    this.propertyObj();
  }
}

export default AddService;
