import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import {
  Header,
  Icon,
  Container,
  Button,
  Table,
  Label,
  Accordion,
  Grid
} from "semantic-ui-react";

const api = new Api();
const cookies = new Cookies();

class AppConfig extends Component {
  state = {
    environment: cookies.get("userEnv"),
    appInfo: "",
    superowner: "",
    core: "",
    mpkg: "",
    storage: "",
    portList: "",
    coreEventConsumer: "",
    mpkgEventConsumer: "",
    loaded: false,
    activeIndex: 0,
    active0: true,
    active1: false,
    active2: false,
    active3: false,
    active4: false
  };

  handleClick0 = (e, titleProps) => {
    // console.log(titleProps);
    // const { index } = titleProps;
    // const { activeIndex } = this.state;
    // const newIndex = activeIndex === index ? -1 : index;

    this.setState({ active0: !this.state.active0 });
  };

  handleClick1 = (e, titleProps) => {
    // console.log(titleProps);
    // const { index } = titleProps;
    // const { activeIndex } = this.state;
    // const newIndex = activeIndex === index ? -1 : index;

    this.setState({ active1: !this.state.active1 });
  };
  handleClick2 = (e, titleProps) => {
    // console.log(titleProps);
    // const { index } = titleProps;
    // const { activeIndex } = this.state;
    // const newIndex = activeIndex === index ? -1 : index;

    this.setState({ active2: !this.state.active2 });
  };
  handleClick3 = (e, titleProps) => {
    // console.log(titleProps);
    // const { index } = titleProps;
    // const { activeIndex } = this.state;
    // const newIndex = activeIndex === index ? -1 : index;

    this.setState({ active3: !this.state.active3 });
  };
  handleClick4 = (e, titleProps) => {
    // console.log(titleProps);
    // const { index } = titleProps;
    // const { activeIndex } = this.state;
    // const newIndex = activeIndex === index ? -1 : index;

    this.setState({ active4: !this.state.active4 });
  };
  getConfigData = res => {
    if (res && res.status === 200) {
      this.setState({
        appInfo: res.data.data.appinfo,
        core: res.data.data.core,
        mpkg: res.data.data.mpkg,
        storage: res.data.data.storage,
        superowner: res.data.data.superowner,
        coreEventConsumer: res.data.data.coreEventConsumer,
        mpkgEventConsumer: res.data.data.mpkgEventConsumer,
        loaded: true
      });
    }
  };

  getPortList = res => {
    if (res && res.status === 200) {
      this.setState({ portList: res.data.data[this.state.environment] });
    }
  };

  setAppInfo() {
    return (
      <Table color="violet">
        <Table.Header>
          <Table.Row>
