import React, { Component } from "react";
import Cookies from "universal-cookie";
import Api from "../../api";
import {
  Modal,
  Accordion,
  Button,
  Icon,
  Menu,
  Container,
  Select,
  Header,
  Segment
} from "semantic-ui-react";
import { BrowserRouter as Link, NavLink } from "react-router-dom";
import LoginView from "../../Login/LoginView";

const cookies = new Cookies();
const myapi = new Api();

class ServiceHanler extends Component {
  state = {
    environment: cookies.get("userEnv"),
    serviceName: this.props.sName
  };

  triggerCommand = (e, { value }) => {
    console.log("!", value);
    let query = "name=" + this.state.serviceName;
    myapi.triggerAndQuery(
      this.state.environment,
      value,
      query,
      this.triggerResponse
    );
  };

  render() {
    return (
      <Menu compact size="mini">
        <Menu.Item>
          <Button
            inverted
            size="small"
            value="build"
            // onClick={this.triggerCommand}
          >
            <span className="btnlabel">Build </span>
            <Icon name="sitemap" circular color="violet" size="small" />
          </Button>
        </Menu.Item>
        <Menu.Item>
          <span className="buttondivider" />
          <Button
            inverted
            size="small"
            value="run"
            // onClick={this.triggerCommand}
          >
            <span className="btnlabel">Start </span>
            <Icon name="play" circular color="violet" size="small" />
          </Button>
        </Menu.Item>
        <Menu.Item>
          <span className="buttondivider" />
          <Button
            inverted
            size="small"
            value="stop"
            // onClick={this.triggerCommand}
          >
            <span className="btnlabel">Stop </span>

            <Icon name="pause" circular color="violet" size="small" />
          </Button>
          <span className="buttondivider" />
        </Menu.Item>
      </Menu>
    );
  }
}

export default ServiceHanler;
